body {
  font: 100% Helvetica, sans-serif;
  color: #3498db;
}

h2 {
  color: #3498db;
}

.container {
  background: #2ecc71;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  -ms-border-radius: 5px;
  border-radius: 5px;
  padding: 20px;
  margin: 10px;
}

nav ul {
  margin: 0;
  padding: 0;
  list-style: none;
}
nav ul li {
  display: inline-block;
  margin-right: 10px;
}
nav ul li a {
  text-decoration: none;
  color: #3498db;
}
nav ul li a:hover {
  color: #2ecc71;
}

